def preprocess(s):
    import numpy as np;
    data_array=[];
    for x in s.split():
        data_array.append(x);
    data_array = np.array(data_array, dtype=np.float64);
    data_array = np.reshape(data_array, (7,39));
    return data_array;

def get_errs(A, yval):
    import numpy as np;
    nrow,_ = A.shape;
    errs = np.array([ np.linalg.norm(A[i,:]-yval, ord=np.inf) for i in range(nrow) ]);
    return errs;

