def get_uniform_weights(s, n, h):
    import numpy as np, math;
    data_array=[];
    for x in s.split():
        data_array.append(1/float(x));
    weights = [ math.factorial(n) * h**n * (-1)**n * data_array[i]
                   for i in range(len(data_array)) ];
    weights = np.array(weights, dtype=np.float64);
    return weights;

def get_Chebyshev_weights(s, n, h):
    import numpy as np;
    data_array=[];
    for x in s.split():
        data_array.append(1/float(x));
    data_array = np.array(data_array, dtype=np.float64);
    return data_array;
